import './mainPage.css';
import React from 'react'

class NameInput extends React.Component{
  constructor(props){
    super(props);
    this.state={
      commentName:""
    }
  }
  render(){
    return(
      <input type="text" placeholder="请输入评论人" onChange={(e)=>{
        this.setState({
          commentName:e.target.value
        })
      }} />
    )
  }
}

class CommentInput extends React.Component{
  constructor(props){
    super(props);
    this.state={
      commentName:""
    }
  }
  render(){
    return(
      <textarea type="text" placeholder="请输入评论内容" onChange={(e)=>{
        this.setState({
          commentName:e.target.value
        })
      }} />
    )
  }
}

class CommentItem extends React.Component{
  constructor(props){
    super(props);
    this.state ={
      commentName:props.commentName,
      commentContent:props.commentContent
    }
  }
  render(){
    return (
      <li>
        <div>评论人：{this.state.commentName}</div>
        <div>评论内容：{this.state.commentContent}</div>
      </li>
    )
  }
}
class MainPage extends React.Component {
  constructor(props) {
    super(props);
    this.state={
      commentList:[
        {commentName:"1",
      commentContent:"真不错"}
      ]
    }
  }

  render() {
    return (
      <div className="MainPage" >
        <div id='inputArea' >
          <NameInput></NameInput>
          <CommentInput id="CommentInput"></CommentInput>
        </div>
        <button>发表评论</button>
        <div>{this.state.commentList.length===0?
          '暂无评论快去评论吧!'
          :<ul>
            {this.state.commentList.map(e=>(
              <CommentItem commentName={e.commentName}commentContent={e.commentContent}></CommentItem>
            ))}
          </ul>
          }
        </div>
      </div>
    );
  }
}

export default MainPage;
