import './mainPage.css';
import React from 'react'

class NameInput extends React.Component{
  constructor(props){
    super(props);
    this.state={
      commentName:""
    }
  }
  render(){
    return(
      <input type="text" placeholder="请输入评论人" onChange={(e)=>{
        this.setState({
          commentName:e.target.value
        })
      }} />
    )
  }
}

class CommentInput extends React.Component{
  constructor(props){
    super(props);
    this.state={
      commentName:""
    }
  }
  render(){
    return(
      <textarea type="text" placeholder="请输入评论内容" onChange={(e)=>{
        this.setState({
          commentName:e.target.value
        })
      }} />
    )
  }
}

class MainPage extends React.Component {
  constructor(props) {
    super(props);
    this.state={}
  }

  render() {
    return (
      <div className="MainPage">
        <Head></Head>
        <div id="body">
        </div>
      </div>
    );
  }
}

export default MainPage;
