import './mainPage.css';
import React from 'react';
import moon from '../../assects/bg.jpg'
class Mouse extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      x: 0,
      y: 0
    }
  }
  componentDidMount() {
    window.addEventListener(
      "mousemove", e => {
        this.setState({
          x: e.clientX,
          y: e.clientY
        })
      }
    )
  }
  render() {
    return this.props.render(this.state);
  }
}
