import React, { useState, useEffect, useRef } from "react";
import "../src/App.css";
function NumberLine() {
  return <div className="number-line preserve translate"></div>;
}
function Char(props) {
  const char = props.char;
  if (char >= "0" && char <= "9") {
    return (
      <div data-digit={char} className="number preserve translate">
        {new Array(7).fill(0).map((e, i) => (
          <NumberLine key={i}></NumberLine>
        ))}
      </div>
    );
  } else if (char === ":") {
    return (
      <div className="char preserve translate" data-before={char}>
        {char}
      </div>
    );
  } else {
    return null;
  }
}
function NumberContainer(props) {
  return (
    <div className="container preserve">
      {[...props.str].map((c, i) => (
        <Char char={c} key={i}></Char>
      ))}
    </div>
  );
}
function App() {
  let [str, setStr] = useState(new Date().toLocaleTimeString());
  let [sc, setSc] = useState("");
  const ref = useRef();
  function handleInput(event) {
    setSc(event.target.value);
  }
  function handleButton() {
    window.open("https://cn.bing.com/search?form=ANNTH1&q=" + sc);
  }
  function enter() {
    return setInterval(() => {
      let date = new Date();
      setStr(date.toLocaleTimeString());
    }, 997);
  }
  useEffect(() => {
    ref.current = enter();
    return () => {
      clearInterval(ref.current);
    };
  });

  return (
    <div id="app">
      <NumberContainer str={str}></NumberContainer>
      <div id="input-container">
        <input
          id="content-input"
          placeholder="请输入搜索内容"
          onInput={handleInput}
          type="text"
        />
        <button onClick={handleButton} id="sbtn">
          搜索
        </button>{" "}
      </div>
      <div id="foot">
        By GrayRabbit@HIT
      </div>
    </div>
  );
}
export default App;
