import './mainPage.css';
import React from 'react'
import bg from '../../assects/bg.jpg'
import moon from '../../assects/moon.png'
import mountain from '../../assects/mountain.png'
class Head extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      moonTop: "50vh",
      moonRight: "5vw",
      mountainBottom: '30vh'
    }
    window.addEventListener('scroll', this.handleScroll);

  }
  handleScroll=e=>{
    this.setState({
      moonRight:((window.scrollY/20)+5)+'vw',
      moonTop:((window.scrollY/50)+50)+'vh',
    })
    if((-(window.scrollY/5)+30)<-55.4) return;
    this.setState({
      mountainBottom:(-(window.scrollY/5)+30)+'vh',
    })
  }
  render() {
    return (
      <div className="Head">
        <section>
          <img id='bg' src={bg} alt="" />
          <img id='moon'style={{top:this.state.moonTop,right:this.state.moonRight}} src={moon} alt="" />
          <div id="txt">给我打钱！</div>
          <img id='mountain' style={{bottom:this.state.mountainBottom}} src={mountain} alt="" />
        </section>
      </div>
    );
  }
}


class MainPage extends React.Component {
  constructor(props) {
    super(props);
    this.state={}
  }

  render() {
    return (
      <div className="MainPage">
        <Head></Head>
        <div id="body">
        </div>
      </div>
    );
  }
}

export default MainPage;
