import './mainPage.css';
import React from 'react'
import bg from '../../assects/bg.jpg'
import moon from '../../assects/moon.png'
import mountain from '../../assects/mountain.png'
class Head extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      moonTop: "20vh",
      moonRight: "5vw",
      mountainBottm: 0
    }
    this.handleScroll = this.handleScroll.bind(this);
    window.addEventListener('scroll', this.handleScroll);

  }
  handleScroll(e) {
    console.log(this);
    this.setState({
      moonRight:((window.srcrollY/100)+5)+'vw'
    })
  }
  render() {
    return (
      <div className="Head">
        <section>
          <img id='bg' src={bg} alt="" />
          <img id='moon'style={{top:this.state.moonTop,right:this.state.moonRight}} src={moon} alt="" />
          <img id='mountain' src={mountain} alt="" />
        </section>
      </div>
    );
  }
}


class MainPage extends React.Component {
  constructor(props) {
    super(props);
    this.state={}
  }

  render() {
    return (
      <div className="MainPage">
        <Head></Head>
        <div id="body">
        </div>
      </div>
    );
  }
}

export default MainPage;
